query.sql:
SELECT id,name,account_type_id,description,creation_datetime,account_number, &pb_entries;.entry_id,&pb_general_ledger;.entry_datetime,&pb_entries;.memorandum, &pb_entries;.entry_id as transaction_id,&pb_general_ledger;.entry_amount, &pb_general_ledger;.account_id,&pb_entry_amounts;.entry_type_id
FROM &pb_accounts;,&pb_entries;,&pb_general_ledger;,&pb_entry_amounts;
WHERE transaction_id=? AND &pb_entries;.entry_id=? AND &pb_accounts;.id=&pb_general_ledger;.account_id AND &pb_entries;.entry_id=&pb_general_ledger;.entry_id AND &pb_entry_amounts;.entry_id=?
